mapper.select:
SELECT
count(0)
FROM
b_trades
WHERE
site_id = #{siteId}
AND (trades_store = #{storeId} or assigned_stores=#{storeId})
AND (
store_user_id = #{adminId}
OR store_shipping_clerk_id = #{adminId}
)
AND( trades_status not in (160,170,180,210,240,900) and deal_finish_status!=1 and is_refund not in (110,120) )

SELECT bt.recevier_name AS recevierName, bt.store_user_id AS storeUserId, bt.trades_id AS tradesId, bt.recevier_mobile AS recevierMobile, date_format(bt.create_time,'%Y-%m-%d %H:%i:%s') AS createTime, bt.receiver_address AS receiverAddress, bt.post_style AS postStyle, bt.trades_status AS tradesStatus, bt.real_pay AS realPay, bte.integral_price AS integralPrice, bte.reduce_reduction_amount AS reduceReductionAmount, bte.user_coupon_id AS userCouponId, bte.user_coupon_amount AS userCouponAmount, bt.post_fee AS postFee, bt.is_refund AS isRefund, bt.self_taken_store AS selfTakenStoreName, bt.assigned_stores AS assignedStoresName, bt.pay_style AS payStyle, bt.recommend_user_id AS storeSalerCode FROM b_trades bt LEFT JOIN b_trades_ext bte ON bt.trades_id=bte.trades_id AND bt.site_id=bte.site_id WHERE bt.buyer_id = #{memberId} AND bt.site_id = #{siteId} AND bt.is_payment=1
AND bt.trades_store = #{storeId}
ORDER BY bt.create_time DESC

