SELECT SUM(tradesSum.num) FROM
(
    SELECT COUNT(*) AS num FROM b_trades WHERE post_style=150 AND trades_status=130 AND to_days(consign_time)=to_days(CURRENT_TIMESTAMP()) AND site_id=#{siteId} AND assigned_stores=#{storeId}
    UNION ALL
    SELECT COUNT(*) AS num FROM b_trades WHERE post_style=150 AND trades_status IN(220,230,800) AND to_days(confirm_goods_time)=to_days(CURRENT_TIMESTAMP()) AND site_id=#{siteId} AND assigned_stores=#{storeId}
    UNION ALL
    SELECT COUNT(*) AS num FROM b_trades WHERE post_style=160 AND trades_status=210 AND to_days(confirm_goods_time)=to_days(CURRENT_TIMESTAMP()) AND site_id=#{siteId} AND assigned_stores=#{storeId}
    UNION ALL
