SELECT trades.date AS `key`, COUNT(*) AS `value` FROM
(
    SELECT trades_id, consign_time, confirm_goods_time, date(consign_time) AS date FROM b_trades WHERE post_style=150 AND trades_status=130 AND left(consign_time,7)=#{month} AND site_id=#{siteId} AND assigned_stores=#{storeId}
    UNION ALL
    SELECT trades_id, consign_time, confirm_goods_time, date(confirm_goods_time) AS date FROM b_trades WHERE post_style=150 AND trades_status IN(220,230,800) AND left(confirm_goods_time,7)=#{month} AND site_id=#{siteId} AND assigned_stores=#{storeId}
    UNION ALL
    SELECT trades_id, consign_time, confirm_goods_time, date(confirm_goods_time) AS date FROM b_trades WHERE post_style=160 AND trades_status=210 AND left(confirm_goods_time,7)=#{month} AND site_id=#{siteId} AND assigned_stores=#{storeId}
    UNION ALL
    SELECT trades_id, consign_time, confirm_goods_time, date(confirm_goods_time) AS date FROM b_trades WHERE post_style=170 AND trades_status=210 AND left(confirm_goods_time,7)=#{month} AND site_id=#{siteId} AND assigned_stores=#{storeId}
) trades GROUP BY trades.date ORDER BY trades.date

