select date(create_time) as query_time,CONVERT(sum(real_pay),DECIMAL(10,2)) as value
from b_trades where site_id=#{siteId}

    and store_user_id=#{storeUserId}


    and assigned_stores=#{storeId}


    and is_payment=#{isPayment}

and create_time between #{start} and #{end}
group by query_time
order by query_time asc

