mapper.select:

select 

from b_contact_person bcp
where bcp.id = #{id,jdbcType=INTEGER}
  and bcp.site_id = #{siteId,jdbcType=INTEGER}

mapper.delete:

delete from b_contact_person
where id = #{id,jdbcType=INTEGER}
  and site_id = #{siteId,jdbcType=INTEGER}

mapper.select:
select *
from b_contact_person
where 1=1

  and member_id = #{memberId,jdbcType=INTEGER}


  and id = #{id,jdbcType=INTEGER}


  and is_default = #{isDefault,jdbcType=INTEGER}


  and is_del=0
order by id DESC
