mapper.select:

select 

from b_servce_template bsd
where bsd.id = #{id,jdbcType=INTEGER}
  and bsd.site_id = #{siteId,jdbcType=INTEGER}

mapper.delete:

delete from b_servce_template
where id = #{id,jdbcType=INTEGER}
  and site_id = #{siteId,jdbcType=INTEGER}

