from b_iconlink
where link_id = #{linkId,jdbcType=INTEGER}
and site_id = #{siteId,jdbcType=INTEGER}

mapper.delete:

delete from b_iconlink
where link_id = #{linkId,jdbcType=INTEGER}
and site_id = #{siteId,jdbcType=INTEGER}

mapper.insert:

