platform_type, create_time, update_time
)
values (#{linkId,jdbcType=INTEGER}, #{siteId,jdbcType=INTEGER}, #{iconId,jdbcType=INTEGER},
#{linkName,jdbcType=VARCHAR}, #{linkUrl,jdbcType=VARCHAR}, #{linkStatus,jdbcType=INTEGER},
#{linkSort,jdbcType=INTEGER}, #{linkItemNum,jdbcType=INTEGER}, #{linkType,jdbcType=INTEGER},
#{platformType,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
