mapper.select:

select 

from b_wx_pic
where pic_id = #{picId,jdbcType=INTEGER}
  and site_id = #{siteId,jdbcType=INTEGER}

mapper.delete:

delete from b_wx_pic
where pic_id = #{picId,jdbcType=INTEGER}
  and site_id = #{siteId,jdbcType=INTEGER}

mapper.update:

update b_wx_pic
set pic_url = #{picUrl,jdbcType=VARCHAR},
  tencent_pic_url = #{tencentPicUrl,jdbcType=VARCHAR},
  status = #{status,jdbcType=TINYINT},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  md5key = #{md5key,jdbcType=VARCHAR},
  media_id = #{mediaId,jdbcType=VARCHAR}
where pic_id = #{picId,jdbcType=INTEGER}
  and site_id = #{siteId,jdbcType=INTEGER}
