mapper.select:
select
,
from yb_51jk_upgrade bso where bso.id = #{id,jdbcType=INTEGER}

mapper.insert:

insert into yb_51jk_upgrade (id, platform_type, version,
title, tips_type, attachment,
status, author, is_del,
create_time, update_time, content
)
values (#{id,jdbcType=INTEGER}, #{platformType,jdbcType=SMALLINT}, #{version,jdbcType=CHAR},
#{title,jdbcType=VARCHAR}, #{tipsType,jdbcType=SMALLINT}, #{attachment,jdbcType=VARCHAR},
#{status,jdbcType=TINYINT}, #{author,jdbcType=CHAR}, #{isDel,jdbcType=TINYINT},
#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
)

mapper.select:

select

,

from yb_51jk_upgrade bso


    bso.id = #{id}


    AND bso.author = #{author}


    AND bso.title = #{title}


    AND bso.platform_type = #{platform_type}


    AND bso.status = #{status}


    AND bso.is_del = #{is_del}


    AND bso.create_time >= #{start_time}


    AND bso.create_time <= #{end_time }


ORDER BY bso.id DESC
